sections.css:
h1.sg,
h2.sg,
h3.sg,
h4.sg,
h5.sg {
  @include default-font;
  font-family: $primary-font;
  font-weight: inherit;
  line-height: 1.2;
  margin: 1.414em 0 .5em;
  color: #444;
}

h1.sg {
  font-size: 3.157em;

  margin-top: .5em;
}

h2.sg {
  font-size: 2.369em;
}

h3.sg {
  font-size: 1.777em;
}

h4.sg {
  font-size: 1.333em;
}

h4.sg,
h5.sg {
  font-weight: 500;
}

a.sg {
  @include default-font;
  display: inline;
  cursor: pointer;
  text-decoration: none;
  color: $default-action-color;
}

a.sg:hover {
  color: lighten($default-action-color, $action-color-change);
}

a.sg:active {
  color: darken($default-action-color, $action-color-change);
}

p.sg {
  @include default-font;
  line-height: 1.5em;
  margin-top: 1.3em;
  margin-bottom: 1.3em;
  color: rgba(#333, 1);
}

code.sg,
pre.sg {
  @include default-font;
  font-family: 'Lucida Console', Monaco, monospace;
  font-size: 1.0em;
  white-space: pre-wrap;
}

li.sg {
  @include default-font;
  margin-left: 1em;
  padding: .2em 0;
}

strong.sg {
  @include default-font;
  font-weight: 700;
}

small.sg {
  @include default-font;
  color: #888;
}

button.sg {
  @include default-font;
  display: inline;

  margin-right: 8px;
  padding: 8px;

  cursor: pointer;

  color: white;
  border-radius: 5px;
  background-color: $default-action-color;

  &:hover {
    background-color: lighten($default-action-color, $action-color-change);
  }
  &:active {
    background-color: darken($default-action-color, $action-color-change);
  }

  &.primary {
    color: white;
    background-color: $primary-action-color;
    &:hover {
      background-color: lighten($primary-action-color, $action-color-change);
    }
    &:active {
      background-color: darken($primary-action-color, $action-color-change);
    }
  }

  &:disabled {
    cursor: default;

    color: #999;
    background-color: $secondary-color;
  }
}

input.sg {
  @include default-font;
  font-family: $secondary-font;
  font-weight: 300;

  margin: 8px;
  padding: 8px;
  padding: .5em;

  border: 1px solid $secondary-color;

  &:focus {
    background-color: $tertiary-color;
  }
}

$mobile: new-breakpoint(max-width 768px);

.sg.wrapper {
  position: relative;
  padding: $wrapper-vertical-padding $content-margin $wrapper-vertical-padding $min-content-margin;
  @include outer-container;
}

.sg.full-height {
  min-height: 100vh;
}

.sg.side-nav {
  padding: 0;
  @include span-columns(3);
  @include media($mobile) {
    @include span-columns(12);
  }
}

.sg.body {
  @include span-columns(9);
  @include media($mobile) {
    padding: 0.25em;

    @include span-columns(12);
  }

  // Remove top margin from first header in markdown
  .sg.heading:nth-of-type(1) {
    margin-top: 0;
    padding-top: 0;
  }
}

.sg.wrapper.collapsed {
  .sg.body {
    @include span-columns(12);
  }
}

.sg.wrapper.desigerToolVisible {
  margin-right: $designer-tool-width;
}

.sg.header {
  width: 100%;
  height: $header-height;
  padding-left: $min-content-margin;
  color: rgba(#fff, 1);
  background: rgba($primary-color, 1);

  pre {
    clear: both;
  }

  .inner {
    height: 100%;

    @include outer-container;
  }

  .title {
    height: 100%;

    @include span-columns(6);

    .logo {
      height: 100%;
      padding: .2em 0;

      @include span-columns(1 of 6);
    }

    h1 {
      font-family: $secondary-font;
      font-size: 1.8em;
      font-weight: 100;
      line-height: $header-height;

      margin: 0;

      color: rgba(#fff, 1);

      @include span-columns(5 of 6);

      span {
        font-size: .9em;
      }
    }
  }

  &.error {
    height: auto;
    background-color: #d2301c;
  }

  &.desigerToolVisible .inner {
    margin-right: $designer-tool-width;
    padding-right: 30px;
  }
}

.sg.footer {
  @include default-font;
  font-weight: 300;

  width: 100%;
  padding: $footer-padding;

  text-align: center;

  color: rgba(#000, .2);
  background: #e2e2e2;
}

.footer .text {
  font-size: 18px;

  color: #fff;
  text-shadow: 0 1px 1px rgba(0,0,0, .3);
}

.sg.footer .logo {
  width: 42px;
  height: $footer-logo-height;
  margin-top: -5px;

  vertical-align: middle;

  -webkit-filter: drop-shadow(0 1px 1px rgba(0,0,0, .3));
}

.sg.side-nav-toggle {
  font-family: $secondary-font;

  position: absolute;
  top: 2.0em;
  left: -1.0em;

  cursor: pointer;

  color: rgba($primary-color, .2);
  border-radius: 5px;

  @include user-select(none);
  @include transition(color .3s ease-in);

  &:before {
    position: absolute;
    top: -1.5em;
    left: -6em;

    width: 10em;

    content: 'Toggle navigation';

    opacity: 0;
    color: rgba($primary-color, .2);

    @include transition(opacity .3s ease-in);
  }

  &:hover {
    color: rgba($primary-color, .7);
    &:before {
      opacity: 1;
    }
  }
}

.sg.side-nav {
  height: 100%;
  padding-left: $min-content-margin;
  vertical-align: top;

  ul {
    list-style: none;
    margin: 2em 0 0 0;
    padding: 0;
    background: rgba(#fff, 1);
  }

  li {
    margin-bottom: .3em;
  }

  li a {
    font-family: $secondary-font;
    line-height: 2em;

    position: relative;

    display: block;

    width: 100%;
    height: 100%;
    padding: .5em;

    color: rgba(#888, 1);


    &:before {
      position: absolute;
      top: 0;
      left: -1em;

      display: block;

      width: 5px;
      height: 100%;

      content: '';

      background: lighten($primary-color, 75%);

      @include transition(all .4s ease-out);
    }

    &:after {
      position: absolute;
      bottom: -.25em;
      left: 0;

      display: block;

      width: 100%;
      height: 1px;

      content: '';

      background: rgba($secondary-color, 1);
    }

    &:hover,
    &.active {
      &:before {
        background: $primary-color;
      }
    }
  }

  .option a {
    display: block;

    padding: .8em;
  }

  .search-section {
    opacity: 1;

    @include transform( translate3d(0, 0, 0) );
    @include transition(all .2s ease);

    label {
      font-family: $secondary-font;
      line-height: 2.5em;

      color: rgba(#000, .5);
    }

    input {
      width: 100%;
      margin: 0;
    }
    &.ng-hide {
      opacity: 0;

      @include transform( translate3d(-5em, 0, 0));
    }
  }
}

.sg.toggle {
  font-family: $secondary-font;
  font-weight: 300;

  cursor: pointer;

  color: rgba($secondary-color, 1);

  &.all-markup {
    margin: 0;
    padding: 1em;
  }
  &.hide-markup {
    position: absolute;
    top: 0;
    right: 0;

    padding: 1em;
  }
  &.show-markup {
    display: block;

    padding: 0;
  }
}

.sg.section-header {
  @include default-font;
  background-color: $primary-color;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;

  a {
    text-decoration: none;
    display: block;
  }

  a:hover {
    i.fa {
      color: rgba($secondary-color, 1);
    }
  }

  h1 {
    color: $secondary-color;
    padding: 13px;
    margin: 0;
    font-size: 1.777em;

    .reference-number {
      color: white;
    }

    .section-source {
      float: right;
      color: rgba($secondary-color, .6);
      font-size: 60%;
      line-height: 1.777em;
    }
  }

  i.fa {
    font-size: 14px;
    padding: 0.4em 0.7em;
    float: right;
    color: rgba($secondary-color, .6);
    @include transition(all .4s ease-out);
  }

}

.sg.section.main {
  .section-header {
    background-color: transparent;

    h1 {
      padding-top: 0;
      font-size: 2.369em;
      color: #333;
    }

    i.fa {
      display: none;
    }
  }

  .reference-number {
    display: none;
  }

  .sg.section-partial, .sg.section-partial:last-child {
    padding-top: 0;
    padding-bottom: 0;
    border: none;
  }
}

.sg.section {
  margin-bottom: 20px;

  .sg.section-partial {
    position: relative;

    overflow: auto;

    padding: 1em;

    border: 1px solid $secondary-color;
    border-bottom: none;
    background: rgba(#fff, 1);
  }

  .sg.section-partial:last-child {
    border-bottom: 1px solid $secondary-color;
  }

  .sg.code-listing {
    padding: 0;
    background-color: #f8f8f8;

    pre {
      padding: 1em;
    }

    .hljs {
      overflow-y: auto;

      max-height: 400px;
    }

    a.show-section {
      display: block;

      padding: .8em 1.2em;

      cursor: pointer;
    }
  }
  .sg.modifier-list {
    @include default-font;
    list-style: none;
    padding: 1em;
    > .item {
      line-height: 1.8em;
      > strong {
        font-weight: bold;
      }
    }
  }
}

.sg.body section.sg.section:last-of-type {
  margin-bottom: 0;
  min-height: calc(100vh - #{$header_height} - #{$footer-logo-height} - #{$wrapper-vertical-padding});
}

@-webkit-keyframes header-highlight {
  0% {
    background-color: $primary-color;
    opacity: 1;
  }
  100% {
    background-color: lighten($primary-color, 15%);
  }
}

.sg.desigerToolVisible .sg.section.active:not(.main) .sg.section-header {
  @include animation(header-highlight 0.3s ease-in 1);
  background-color: lighten($primary-color, 17%);
}

.sg.label {
  @include default-font;
  position: absolute;
  z-index: 8000;
  top: 0;
  right: 0;

  border: 1px solid $secondary-color;
  border-top: none;
  border-right: none;
  background: rgba(#fff, .9);

  span,
  p {
    @include default-font;
    font-weight: 500;

    display: inline;

    padding: 0 .8em 0 0;

    text-decoration: none;
  }

  a {
    @include default-font;
    display: inline-block;

    padding: .4em .7em;

    cursor: pointer;
    text-decoration: none;
  }

  a:hover {
    i.fa {
      color: rgba($primary-color, .7);
      &:after {
        opacity: 1;
      }
    }
  }

  i.fa {
    cursor: pointer;

    color: rgba($primary-color, .2);

    @include transition(all .4s ease-out);
  }
}

.sg.design {
  position: fixed;
  top: 0;
  left: 100%;

  width: $designer-tool-width;
  height: 100%;

  z-index: 9000;

  @include media($mobile) {
    width: $designer-tool-width-mobile;
  }

  border-left: 1px solid rgba(#000, .3);

  @include transition(left .3s ease-out);

  .handle {
    @include default-font;
    position: absolute;
    top: 220px;
    left: -30px;

    width: 150px;
    height: 30px;
    padding: 5px 10px;

    cursor: pointer;
    text-align: center;

    color: rgba(#000, .4);
    border-top: 1px solid rgba(#000, .3);
    border-right: 1px solid rgba(#000, .3);
    border-left: 1px solid rgba(#000, .3);
    border-radius: 3px 3px 0 0;
    background: rgba(#fff, 1);

    @include transition(left .3s ease-out);
    @include transition-delay(.3s);
    @include transform(rotate(-90deg));
    @include transform-origin(left top);
  }

  .close {
    position: absolute;
    top: 0;
    right: 0;

    padding: 10px;
  }

  &.sg-hidden {
    left: 100%;
  }

  &.sg-visible {
    left: 100% - $designer-tool-width;
    @include media($mobile) {
      left: 100% - $designer-tool-width-mobile;
    }

    .handle {
      left: 0;
    }
  }

  .info-text {
    margin-top: 10px;
  }
}

.sg.design-content {
  @include default-font;
  position: relative;
  z-index: 9000;

  overflow-x: auto;
  overflow-y: auto;

  width: 100%;
  height: 100%;
  padding: 1em;

  background: white;

  ul {
    margin: 0;
    padding: 0;
    list-style: none;
  }

  label {
    display: block;

    i.fa {
      padding-left: 8px;
      color: red;
    }

    a {
      i.fa.fa-eye {
        font-size: 16px;
        padding-left: 4px;
        color: rgba($primary-color, .2);
        @include transition(all .4s ease-out);
      }

      &:hover {
        i.fa.fa-eye {
          color: rgba($primary-color, .8);
        }
      }
    }

    .variable-source {
      opacity: 0.5;
      font-size: 80%;
    }

  }

  input {
    display: inline;
  }

  input[type='text'] {
    box-sizing: border-box;
    width: 100%;
  }

  input[type='color'] {
    background: transparent;
    position: absolute;
    right: 0;

    box-sizing: border-box;
    width: 36px;
    height: 36px;
    padding: 0;

    cursor: pointer;

    border: none;
  }
}

#ngProgress {
  z-index: 99998;

  height: 4px;
  margin: 0;
  padding: 0;
  /* Add CSS3 styles for transition smoothing */

  -webkit-transition: all .5s ease-in-out;
     -moz-transition: all .5s ease-in-out;
       -o-transition: all .5s ease-in-out;
          transition: all .5s ease-in-out;

  opacity: 0;
  color: $primary-action-color;
  background-color: $primary-action-color;
  box-shadow: 0 0 3px 0; /* Inherits the font color */
}

#ngProgress-container {
  position: fixed;
  z-index: 99999;
  top: 0;
  right: 0;
  left: 0;

  margin: 0;
  padding: 0;
}

.sg#socketDisconnection {
  position: fixed;
  z-index: 9100;
  top: 0;
  right: $designer-tool-width + 2%;

  @include transition(top .2s ease-in);
}

.sg#socketDisconnection.ng-hide {
  top: -60px;
}

.sg.disconnection-icon {
  line-height: 32px;

  width: 32px;
  height: 32px;
  padding-top: 3px;

  text-align: center;
  vertical-align: middle;

  color: #fff;
  border-radius: 3px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  background-color: #d20000;
  i.fa {
    font-size: 20px;

    @include animation(socket-disconnect-pulse 3s linear infinite);
  }
}

@include keyframes(socket-disconnect-pulse) {
  80% {
    color: rgba(255, 255, 255, 1);
  }
  90% {
    color: rgba(255, 255, 255, .35);
  }
  100% {
    color: rgba(255, 255, 255, 1);
  }
}

.styleguide-color {
  @include default-font;
  font-weight: 300;

  display: block;

  width: 100%;
  height: 5em;
  margin-bottom: .5em;
  padding: 2em;

  text-align: center;

  color: rgba(#fff, 1);
  border-radius: 5px;
  text-shadow: #000 0 0 2px;
}

.styleguide-grid-unit {
  min-height: 3em;
  padding: 1em;

  border: 2px solid rgba(#000, .5);
  background: rgba(#000, .1);
}

sg-common-class-wrapper {
  box-sizing: border-box;
  @include reset;
}
