sections.css:
$color-copy: #464646;
$color-highlight: #3dc7a6;
$color-highlight-soften: #77d8c1; // tint($color-highlight, 30%);
$color-highlight-darken: #2B8B74; // shade($color-highlight, 30%);
$color-tooltip: $color-copy;

// ----- Base Content ----- //

$base-maxWidth: 62.5em; // ~1000px
$base-fontSize: 16px;
$base-lineHeight: 1.4;
$base-whitespace: 20px;
$base-whitespace-xs: 0.25 * $base-whitespace;
$base-whitespace-s: 0.5 * $base-whitespace;
$base-whitespace-l: 2 * $base-whitespace;
$base-whitespace-xl: 4 * $base-whitespace;


// ----- Fonts ----- //

$ff-copy: sans-serif;
$ff-heading: sans-serif;


// ----- Breakpoints ----- //

$bp-tablet: 640px;


// ----- Z-index ----- //

$z-index-tooltip: 1000;
$z-index-modal: 2000;

.button {
	display: inline-block;
	vertical-align: middle;
	font: inherit;
	cursor: pointer;
	border: none;
	text-align: center;
	margin: 0;
	padding: 8px 16px;
	border-radius: 3px;
	background-color: $color-highlight;

	&, 
	&:hover,
	&:active, 
	&:focus {
		text-decoration: none;
		color: #fff;
	}

	&:hover,
	&:active, 
	&:focus {
		background-color: $color-highlight-darken;
	}
}


// -------------------------------------
// Modifiers
// -------------------------------------

.button--soft {
	border-radius: 200px;
}

.button--hard {
	border-radius: 0;
}

.button--full {
	width: 100%;
}

// -------------------------------------
// States
// -------------------------------------

.button {
	&[disabled="disabled"],
	&.is-disabled {
	    pointer-events: none;
	    cursor: default;
	    background-color: fade-out($color-copy, 0.85);
	    color: fade-out(white, 0.4);
	}
}

.media {
    display: block;
    @include clearfix;
}

// -------------------------------------
// Modifiers
// -------------------------------------

.media--rev {
    > .media-img {
        float: right;
        margin-right: 0;
        margin-left: $base-whitespace;
    }
}

@include respond-to($bp-tablet, max-width) {
    .media--responsive {
        > .media-img {
            float: none;
            margin-right: 0;
            margin-bottom: $base-whitespace;
            margin-left: 0;
        }
    }
}

// -------------------------------------
// Submodules
// -------------------------------------

.media-img {
    float: left;
    margin-right: $base-whitespace;

    > img {
        display: block;
    }
}

.media-body {
    overflow: hidden;
    display: block;

    &, > :last-child {
        margin-bottom: 0;
    }
}

.tooltip {
	position: absolute;
	z-index: $z-index-tooltip;
	display: block;
	visibility: visible;
	font-size: 12px;
	line-height: 1.2;
}


// -------------------------------------
// Modifiers
// -------------------------------------

.tooltip--top {
	margin-top: -3px; 
	padding: 5px 0;

	.tooltip-arrow {
	    bottom: 0;
	    left: 50%;
	    margin-left: -5px;
	    border-width: 5px 5px 0;
	    border-top-color: $color-tooltip;
	}
}

.tooltip--right {
	margin-left: 3px; 
	padding: 0 5px;

	 .tooltip-arrow {
	    top: 50%;
	    left: 0;
	    margin-top: -5px;
	    border-width: 5px 5px 5px 0;
	    border-right-color: $color-tooltip;
	}
}

.tooltip--bottom {
	margin-top: 3px; 
	padding: 5px 0;

	.tooltip-arrow {
	    top: 0;
	    left: 50%;
	    margin-left: -5px;
	    border-width: 0 5px 5px;
	    border-bottom-color: $color-tooltip;
	}
}

.tooltip--left {
	margin-left: -3px; 
	padding: 0 5px;

	.tooltip-arrow {
	    top: 50%;
	    right: 0;
	    margin-top: -5px;
	    border-width: 5px 0 5px 5px;
	    border-left-color: $color-tooltip;
	}
}


// -------------------------------------
// Submodules
// -------------------------------------

.tooltip-content {
	max-width: 200px;
	padding: 3px 8px;
	color: #FFF;
	text-align: center;
	text-decoration: none;
	background-color: $color-tooltip;
	border-radius: 3px;
}

.tooltip-arrow {
    position: absolute;
    width: 0;
    height: 0;
    border-color: transparent;
    border-style: solid;
}

.pager {
	text-align: center;

	li {
		display: inline;
	    margin-right: 8px;

	    &.is-active a { background-color: $color-copy; }

	    a {
			border-radius: 6px;
			border: 2px solid $color-copy;
			background-color: transparent;
			display: inline-block;
			height: 12px;
			overflow: hidden;
			text-indent: -9999px;
			width: 12px;
		}
	}		
}


// -------------------------------------
//   Modifiers
// -------------------------------------

.pager--inverse {
	li {
		&.is-active a { background-color: #FFF; }
		a { border-color: #FFF; }
	}
}

.modal {
  display: block;
  overflow: auto;
  overflow-y: scroll;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: $z-index-modal;
  background-color: fade-out(black, 0.8);
}


// -------------------------------------
// Submodules
// -------------------------------------

.modal-content {
  position: relative;
  max-width: 460px;
  width: 70%;
  margin: 0 auto;
  background-color: white;
  border-radius: 3px;
  box-shadow: 1px 1px 6px rgba(0, 0, 0, 0.2);
  outline: 0;
}

.modal-header {
  padding: 20px;
  font-size: 20px;
  font-weight: bold;
}

.modal-body {
  position: relative;
  padding: 20px;
  overflow: auto;
}

.modal-footer {
  text-align: right;
  padding: 20px;
}
