sections.css:
$color-copy: #464646;
$color-highlight: #3dc7a6;
$color-soften: #77d8c1; // tint($color-highlight, 30%);
$color-darken: #2B8B74; // shade($color-highlight, 30%);
$color-tooltip: $color-copy;


// ----- Base Content ----- //

$base-maxWidth: 62.5em; // ~1000px
$base-fontSize: 16px;
$base-lineHeight: 1.4;
$base-whitespace: 20px;
$base-whitespace-xs: 0.25 * $base-whitespace;
$base-whitespace-s: 0.5 * $base-whitespace;
$base-whitespace-l: 2 * $base-whitespace;
$base-whitespace-xl: 4 * $base-whitespace;


// ----- Fonts ----- //

$ff-copy: sans-serif;
$ff-heading: sans-serif;


// ----- Z-index ----- //

$zindex-tooltip: 1000;

.button {
	display: inline-block;
	vertical-align: middle;
	white-space: nowrap;
	font-family: inherit;
	font-size: 100%;
	cursor: pointer;
	border: none;
	margin: 0;
	padding-top: 0;
	padding-bottom: 0;
	line-height: 3;
	padding-right: 1em;
	padding-left: 1em;
	border-radius: 3px;
	background-color: $color-highlight;
	color: #fff;

	&, 
	&:hover {
		text-decoration: none;
	}

	&:hover,
	&:active, 
	&:focus {
		background-color: $color-darken;
	}

	&:active, 
	&:focus {
		outline:none;
	}
}


// -------------------------------------
// Modifiers
// -------------------------------------

.button--soft {
	border-radius: 200px;
}

.button--hard {
	border-radius: 0;
}

.media {
    display: block;
    @include clearfix;
}

// -------------------------------------
// Modifiers
// -------------------------------------

.media-img--rev {
    float: right;
    margin-left: $base-whitespace;
}

// -------------------------------------
// Submodules
// -------------------------------------

.media-img {
    float: left;
    margin-right: $base-whitespace;

    > img {
        display: block;
    }
}

.media-body {
    overflow: hidden;
    display: block;

    &, > :last-child {
        margin-bottom: 0;
    }
}

.tooltip {
	position: absolute;
	z-index: $zindex-tooltip;
	display: block;
	visibility: visible;
	font-size: 12px;
	line-height: 1.2;
}


// -------------------------------------
// Modifiers
// -------------------------------------

.tooltip--top {
	margin-top: -3px; 
	padding: 5px 0;

	.tooltip-arrow {
	    bottom: 0;
	    left: 50%;
	    margin-left: -5px;
	    border-width: 5px 5px 0;
	    border-top-color: $color-tooltip;
	}
}

.tooltip--right {
	margin-left: 3px; 
	padding: 0 5px;

	 .tooltip-arrow {
	    top: 50%;
	    left: 0;
	    margin-top: -5px;
	    border-width: 5px 5px 5px 0;
	    border-right-color: $color-tooltip;
	}
}

.tooltip--bottom {
	margin-top: 3px; 
	padding: 5px 0;

	.tooltip-arrow {
	    top: 0;
	    left: 50%;
	    margin-left: -5px;
	    border-width: 0 5px 5px;
	    border-bottom-color: $color-tooltip;
	}
}

.tooltip--left {
	margin-left: -3px; 
	padding: 0 5px;

	.tooltip-arrow {
	    top: 50%;
	    right: 0;
	    margin-top: -5px;
	    border-width: 5px 0 5px 5px;
	    border-left-color: $color-tooltip;
	}
}


// -------------------------------------
// Submodules
// -------------------------------------

.tooltip-content {
	max-width: 200px;
	padding: 3px 8px;
	color: #FFF;
	text-align: center;
	text-decoration: none;
	background-color: $color-tooltip;
	border-radius: 3px;
}

.tooltip-arrow {
    position: absolute;
    width: 0;
    height: 0;
    border-color: transparent;
    border-style: solid;
}
